SELECT COUNT(1) 
  FROM BAS_APPLICATION_CATEGORY 
 WHERE acc = #{acc}
   AND acc_code = #{acc_code}
   AND language = #{language}

		select status_code,acc_code,acc_description 
		from BAS_APPLICATION_CATEGORY
		where acc = #{acc}
   	    and acc_code = #{acc_code}
   	    and language = #{language}

   		select count(*)
   			from BAS_APPLICATION_CATEGORY 
   		where 1=1 

			and acc = #{acc}



			and acc_code like '%' #{acc_code} '%'



			and acc_description like '%' #{acc_description} '%'	


			and language = #{language}


			and status_code = #{status_code}

   		select status_code,acc_code, acc_description
   			from BAS_APPLICATION_CATEGORY 
   		where 1=1 

			and acc = #{acc}


			and acc_code like '%' #{acc_code} '%'


			 and acc_description like '%' #{acc_description} '%'	


			and language = #{language}


			and status_code = #{status_code}

		order by LAST_MODIFIED_DATE desc limit #{startIndex}, #{pageSize}

